"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _Base64 extends Matcher {
  constructor(...params) {
    super("base64");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    const base64Req = /^(?:[A-Za-z0-9+/]{4})*(?:[A-Za-z0-9+/]{2}==|[A-Za-z0-9+/]{3}=|[A-Za-z0-9+/]{4})$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(base64Req)) {
        this.result.addInvalidValueError("base64.e001", "The value must be string containing base64 encoded value.");
      }
    } else {
      this.result.addInvalidTypeError("base64.e001", "The value must be string containing base64 encoded value.");
    }

    return this;
  }
}

function Base64(...params) {
  let matcher = new _Base64(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Base64;
