"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _DataKey extends Matcher {
  constructor(...params) {
    super("dataKey");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    const dataKeyReq = /^[0-9a-zA-Z_-]{3,256}$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(dataKeyReq)) {
        this.result.addInvalidValueError("dataKey.e001", "The value must be dataKey.");
      }
    } else {
      this.result.addInvalidTypeError("dataKey.e001", "The value must be dataKey.");
    }

    return this;
  }
}

function DataKey(...params) {
  let matcher = new _DataKey(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = DataKey;
