"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _UpperCamelCase extends Matcher {
  constructor(...params) {
    super("upperCamelCase");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    //https://google.github.io/styleguide/javaguide.html#s5.3-camel-case
    const UpperCamelCaseReq = /^((\d)|([A-Z0-9][a-z0-9]+))*([A-Z])?$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(UpperCamelCaseReq)) {
        this.result.addInvalidValueError("upperCamelCase.e001", "The value must be upperCamelCase.");
      }
    } else {
      this.result.addInvalidTypeError("upperCamelCase.e001", "The value must be upperCamelCase.");
    }

    return this;
  }
}

function UpperCamelCase(...params) {
  let matcher = new _UpperCamelCase(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = UpperCamelCase;
