"use strict";

/**
 * Parent class for all matchers
 */
class Matcher {
  constructor(name, validateArgs = null, matchValue) {
    this.name = name;
    this.validateArgs = validateArgs;
    this.matchValue = matchValue;
  }

  /**
   * validate arguments
   * this method is net required to be implemented
   * @param args
   */
  validateArgs(...args) {
    if (this.validateArgs === null) {
      if (args && args.length > 0) {
        throw new Error("Wrong number of arguments, expected 0");
      }
    }
  }

  /**
   * This method contains whole validation process
   * @param {Object} value
   * @param {Object} ctx
   * @param {Object} args
   */
  match(value, ctx, args) {
    throw new Error("method not implemented");
  }
}

module.exports = Matcher;
