"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _Boolean extends Matcher {
  constructor(...params) {
    super("boolean");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    if (typeof value === "boolean") {
    } else {
      this.result.addInvalidTypeError("boolean.e001", "The value must be boolean.");
    }

    return this;
  }
}

function BooleanMatcher(...params) {
  let matcher = new _Boolean(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = BooleanMatcher;
