"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _Code extends Matcher {
  constructor(...params) {
    super("code");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    const codeReq = /^[0-9a-zA-Z_]{3,64}$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(codeReq)) {
        this.result.addInvalidValueError("code.e001", "The value must be code.");
      }
    } else {
      this.result.addInvalidTypeError("code.e001", "The value must be code.");
    }

    return this;
  }
}

function Code(...params) {
  let matcher = new _Code(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Code;
