"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _Gps extends Matcher {
  constructor(...params) {
    super("gps");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    const gpsReq = /^[-+]?([1-8]?\d\.\d+|90\.0+),\s*[-+]?(180\.0+|((1[0-7]\d)|([1-9]?\d))\.\d+)$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(gpsReq)) {
        this.result.addInvalidValueError("gps.e001", "The value must match GPS coordinates.");
      }
    } else {
      this.result.addInvalidTypeError("gps.e001", "The value must match GPS coordinates.");
    }

    return this;
  }
}

function Gps(...params) {
  let matcher = new _Gps(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Gps;
