"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _Hexa64Code extends Matcher {
  constructor(...params) {
    super("hexa64Code");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    const hexa64CodeReg = /^[0-9a-f]{64}$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(hexa64CodeReg)) {
        this.result.addInvalidValueError("hexa64Code.e001", "The value must be 64 characters long hexadecimal code.");
      }
    } else {
      this.result.addInvalidTypeError("hexa64Code.e001", "The value must be 64 characters long hexadecimal code.");
    }

    return this;
  }
}

function Hexa64Code(...params) {
  let matcher = new _Hexa64Code(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Hexa64Code;
