"use strict";

const Matcher = require("../matcher");
const ValidationResult = require("../validation-result");
const Validator = require("../validator");

class _MongoId extends Matcher {
  constructor(...params) {
    super("mongoId");
    this.params = params;
  }

  match(value, ctx, ...args) {
    this.result = new ValidationResult();

    if (value == undefined) {
      return this;
    }

    const mongoIdReq = /^[0-9a-f]{24}$/;

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(mongoIdReq)) {
        this.result.addInvalidValueError("mongoId.e001", "The value must be mongoId.");
      }
    } else {
      if (typeof value !== "object" || !value.constructor || value.constructor.name !== "ObjectID") {
        this.result.addInvalidTypeError("mongoId.e001", "The value must be mongoId.");
      }
    }

    return this;
  }
}

function MongoId(...params) {
  let matcher = new _MongoId(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = MongoId;
